import React, { Component } from "react";
import withStyles from "@material-ui/styles/withStyles";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import CardItem from "./cards/CardItem";
import Topbar from "./Topbar";
import SectionHeader from "./typo/SectionHeader";
import  AuthHandler  from "./common/AuthHandler";

// const backgroundShape = require("../images/shape.svg");

const styles = theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.grey["a2a2a2"],
    overflow: "hidden",
    // background: `url(${backgroundShape}) no-repeat`,
    backgroundSize: "cover",
    backgroundPosition: "0 400px",
    // marginTop: 20,
    padding: 20,
    // paddingBottom: 200
  },
  grid: {
    width: 1000
  }
});

class Users extends Component {
  getUsers(param) {
    this.checkIsUserLoggedIn();
    let apiURI = '';
    if (param) {
      apiURI = Config.API.BASE_URL + Config.API.GET_ALL_CLIENTS + `?page=${param.page}&per_page=${param.perPage}`;
    }
    else {
      apiURI = Config.API.BASE_URL + Config.API.GET_ALL_CLIENTS;
    }
    let authHandler = new AuthHandler();
    let isAuthenticated = authHandler.isAuthenticated();

    // this.getOrderCount(apiURI)

    this.setState({ loader: true });
    fetch(apiURI, {
      method: "GET",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json",
        "Authorization": "Bearer " + isAuthenticated,
        "cache-control": "no-cache"
      }
    }).then(response => {
      response.json()
        .then(responseJson => {

          if (response.status === 401) {
            this.props.history.push("/login");
          }
          if (!responseJson.error === null) {
            throw responseJson;
          }
          this.setState({ data: responseJson.data });
          this.setState({ _meta: responseJson._meta });
          this.setState({ loader: false });
        })
        .catch(Err => {
          if (response.status === 401) {
            this.props.history.push("/login");
          }
          this.setState({ displayErrors: true });
          this.setState({ loader: false });
        })
    }
    );
  }


  componentDidMount() {
    window.scrollTo(0, 0);
  }

  render() {
    const { classes } = this.props;
    const currentPath = this.props.location.pathname;

    return (
      <React.Fragment>
        <CssBaseline />
        <Topbar currentPath={currentPath} />
        <div className={classes.root}>
          <Grid container justify="center">
            <Grid
              spacing={1}
              alignItems="center"
              justify="center"
              container
              className={classes.grid}
            >
              <Grid item xs={12}>
                <SectionHeader
                  title="Customers"
                  subtitle=""
                />
                <CardItem />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(Users);
