import React, { Component } from "react";
import withStyles from "@material-ui/styles/withStyles";
import { withRouter } from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Back from "./common/Back";

const qs = require("query-string");
const backgroundShape = require("../images/shape.svg");

const numeral = require("numeral");
numeral.defaultFormat("0,000");

const styles = theme => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.primary["A100"],
        overflow: "hidden",
        background: `url(${backgroundShape}) no-repeat`,
        backgroundSize: "cover",
        backgroundPosition: "0 400px",
        marginTop: 10,
        padding: 20,
        paddingBottom: 200
    },
    grid: {
        margin: `0 ${theme.spacing(2)}px`
    },
    smallContainer: {
        width: "60%"
    },
    bigContainer: {
        width: "80%"
    },
    stepContainer: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    stepGrid: {
        width: "80%"
    },
    backButton: {
        marginRight: theme.spacing(1)
    },
    outlinedButtom: {
        textTransform: "uppercase",
        margin: theme.spacing(1)
    },
    stepper: {
        backgroundColor: "transparent"
    },
    paper: {
        padding: theme.spacing(3),
        textAlign: "left",
        color: theme.palette.text.secondary
    },
    topInfo: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: 42
    },
    formControl: {
        width: "100%"
    },
    selectEmpty: {
        marginTop: theme.spacing(2)
    },
    borderColumn: {
        borderBottom: `1px solid ${theme.palette.grey["100"]}`,
        paddingBottom: 24,
        marginBottom: 24
    },
    flexBar: {
        marginTop: 32,
        display: "flex",
        justifyContent: "center"
    }
});

const getSteps = () => {
    return ["Info", "Bank", "Loan details", "Terms", "Confirm", "Done"];
};

class UserDetails extends Component {
    state = {
        activeStep: 0,
        receivingAccount: "Home Account",
        repaimentAccount: "Saving Account",
        termsChecked: false,
        labelWidth: 0
    };

    componentDidMount() { }

    goToDashboard = event => {
        const queryString = this.props.location.search;

        this.props.history.push({
            pathname: "/",
            search: queryString
        });
    };

    render() {
        const { classes } = this.props;
        const queryString = this.props.location.search;
        const parsed = queryString ? qs.parse(queryString) : {};
        const steps = getSteps();
        const { activeStep } = this.state;

        return (
            <React.Fragment>
                <CssBaseline />
                <div className={classes.root}>
                    <Grid container justify="center">
                        <Grid
                            spacing={10}
                            alignItems="center"
                            justify="center"
                            container
                            className={classes.grid}
                        >
                            <Grid item xs={12}>
                                <Back />
                                <div className={classes.stepContainer}>
                                    <div className={classes.bigContainer}>
                                        <Stepper
                                            classes={{ root: classes.stepper }}
                                            activeStep={activeStep}
                                            alternativeLabel
                                        >
                                            {steps.map(label => {
                                                return (
                                                    <Step key={label}>
                                                        <StepLabel>{label}</StepLabel>
                                                    </Step>
                                                );
                                            })}
                                        </Stepper>
                                    </div>
                                    {activeStep === 0 && (
                                        <div className={classes.bigContainer}>
                                            <Paper className={classes.paper}>
                                                <div className={classes.topInfo}>
                                                    <div>
                                                        <Typography
                                                            variant="subtitle1"
                                                            style={{ fontWeight: "bold" }}
                                                            gutterBottom
                                                        >
                                                            Information
                            </Typography>
                                                        <Typography variant="body1" gutterBottom>
                                                            General information about the service
                            </Typography>
                                                    </div>
                                                    <div>
                                                        <Button
                                                            variant="outlined"
                                                            size="large"
                                                            className={classes.outlinedButtom}
                                                        >
                                                            Edit
                            </Button>
                                                    </div>
                                                </div>
                                                <Grid item container xs={12}>
                                                    <Grid item xs={6}>
                                                        <Typography
                                                            style={{ textTransform: "uppercase" }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            User
                            </Typography>
                                                        <Typography variant="h5" gutterBottom>
                                                            John Doe
                            </Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography
                                                            style={{ textTransform: "uppercase" }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            City
                            </Typography>
                                                        <Typography variant="h5" gutterBottom>
                                                            Tokyo
                            </Typography>
                                                    </Grid>
                                                </Grid>
                                            </Paper>
                                        </div>
                                    )}
                                    {activeStep === 1 && (
                                        <div className={classes.smallContainer}>
                                            <Paper className={classes.paper}>
                                                <div>
                                                    <div style={{ marginBottom: 32 }}>
                                                        <Typography
                                                            variant="subtitle1"
                                                            style={{ fontWeight: "bold" }}
                                                            gutterBottom
                                                        >
                                                            Bank information
                            </Typography>
                                                        <Typography variant="body1" gutterBottom>
                                                            Select account to receive the money
                            </Typography>
                                                    </div>
                                                    <div style={{ marginBottom: 32 }}>
                                                        <Typography
                                                            style={{ textTransform: "uppercase" }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            Bank
                            </Typography>
                                                        <Typography variant="h5" gutterBottom>
                                                            N26
                            </Typography>
                                                    </div>
                                                    <div>
                                                        <Typography
                                                            style={{
                                                                textTransform: "uppercase",
                                                                marginBottom: 20
                                                            }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            Receiving account
                            </Typography>
                                                        <FormControl
                                                            variant="outlined"
                                                            className={classes.formControl}
                                                        >
                                                            <Select
                                                                value={this.state.receivingAccount}
                                                                onChange={this.handleChange}
                                                                input={
                                                                    <OutlinedInput
                                                                        labelWidth={this.state.labelWidth}
                                                                        name="receivingAccount"
                                                                    />
                                                                }
                                                            >
                                                                <MenuItem value="">
                                                                    <em></em>
                                                                </MenuItem>
                                                                <MenuItem value={"0297 00988200918"}>
                                                                    First account
                                </MenuItem>
                                                                <MenuItem value={"0235 00235233332"}>
                                                                    Second account
                                </MenuItem>
                                                                <MenuItem value={"1256 00864222212"}>
                                                                    Third account
                                </MenuItem>
                                                            </Select>
                                                        </FormControl>
                                                    </div>
                                                </div>
                                            </Paper>
                                        </div>
                                    )}
                                    {activeStep === 2 && (
                                        <div className={classes.bigContainer}>
                                            <Paper className={classes.paper}>
                                                <div className={classes.topInfo}>
                                                    <div>
                                                        <Typography
                                                            variant="subtitle1"
                                                            style={{ fontWeight: "bold" }}
                                                            gutterBottom
                                                        >
                                                            Details
                            </Typography>
                                                        <Typography variant="body1" gutterBottom>
                                                            We need some details about any information
                            </Typography>
                                                    </div>
                                                    <div>
                                                        <Button
                                                            variant="outlined"
                                                            size="large"
                                                            className={classes.outlinedButtom}
                                                        >
                                                            Edit
                            </Button>
                                                    </div>
                                                </div>
                                                <div className={classes.borderColumn}>
                                                    <Grid
                                                        item
                                                        container
                                                        xs={12}
                                                        style={{ marginBottom: 32 }}
                                                    >
                                                        <Grid item xs={6}>
                                                            <Typography
                                                                style={{ textTransform: "uppercase" }}
                                                                color="secondary"
                                                                gutterBottom
                                                            >
                                                                Amount
                              </Typography>
                                                            <Typography variant="h5" gutterBottom>
                                                                {parsed
                                                                    ? numeral(parsed.amount).format()
                                                                    : "75,000"}{" "}
                                DKK
                              </Typography>
                                                        </Grid>
                                                        <Grid item xs={6}>
                                                            <Typography
                                                                style={{ textTransform: "uppercase" }}
                                                                color="secondary"
                                                                gutterBottom
                                                            >
                                                                Total fees
                              </Typography>
                                                            <Typography variant="h5" gutterBottom>
                                                                0 DKK
                              </Typography>
                                                        </Grid>
                                                    </Grid>
                                                    <Grid item container xs={12}>
                                                        <Grid item xs={6}>
                                                            <Typography
                                                                style={{ textTransform: "uppercase" }}
                                                                color="secondary"
                                                                gutterBottom
                                                            >
                                                                Total price
                              </Typography>
                                                            <Typography variant="h5" gutterBottom>
                                                                {parsed
                                                                    ? numeral(parsed.interest).format()
                                                                    : "6,600"}{" "}
                                USD
                              </Typography>
                                                        </Grid>
                                                        <Grid item xs={6}>
                                                            <Typography
                                                                style={{ textTransform: "uppercase" }}
                                                                color="secondary"
                                                                gutterBottom
                                                            >
                                                                Total cost
                              </Typography>
                                                            <Typography variant="h5" gutterBottom>
                                                                {parsed
                                                                    ? numeral(parsed.cost).format()
                                                                    : "81,600"}{" "}
                                USD
                              </Typography>
                                                        </Grid>
                                                    </Grid>
                                                </div>
                                                <Grid item container xs={12}>
                                                    <Grid
                                                        item
                                                        container
                                                        xs={12}
                                                        style={{ marginBottom: 32 }}
                                                    >
                                                        <Grid item xs={6}>
                                                            <Typography
                                                                style={{ textTransform: "uppercase" }}
                                                                color="secondary"
                                                                gutterBottom
                                                            >
                                                                How often
                              </Typography>
                                                            <Typography variant="h5" gutterBottom>
                                                                Once a month
                              </Typography>
                                                        </Grid>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography
                                                            style={{ textTransform: "uppercase" }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            When to start
                            </Typography>
                                                        <Typography variant="h5" gutterBottom>
                                                            01 February 2019
                            </Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography
                                                            style={{ textTransform: "uppercase" }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            When it ends?
                            </Typography>
                                                        <Typography variant="h5" gutterBottom>
                                                            01 May 2019
                            </Typography>
                                                    </Grid>
                                                </Grid>
                                                <Grid item container xs={12} style={{ marginTop: 24 }}>
                                                    <Grid item xs={6}>
                                                        <Typography
                                                            style={{
                                                                textTransform: "uppercase",
                                                                marginBottom: 20
                                                            }}
                                                            color="secondary"
                                                            gutterBottom
                                                        >
                                                            Destination account
                            </Typography>
                                                        <FormControl
                                                            variant="outlined"
                                                            className={classes.formControl}
                                                        >
                                                            <Select
                                                                value={this.state.repaimentAccount}
                                                                onChange={this.handleChange}
                                                                input={
                                                                    <OutlinedInput
                                                                        labelWidth={this.state.labelWidth}
                                                                        name="repaimentAccount"
                                                                    />
                                                                }
                                                            >
                                                                <MenuItem value="">
                                                                    <em></em>
                                                                </MenuItem>
                                                                <MenuItem value={"0297 00988200918"}>
                                                                    Account one
                                </MenuItem>
                                                                <MenuItem value={"0235 00235233332"}>
                                                                    Account two
                                </MenuItem>
                                                                <MenuItem value={"1256 00864222212"}>
                                                                    Other account
                                </MenuItem>
