import React, { Component } from "react";
import withStyles from "@material-ui/styles/withStyles";
import { withRouter } from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';


import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import Back from "./common/Back";
import { Link } from "react-router-dom";

const qs = require("query-string");
// const backgroundShape = require("../images/shape.svg");

const numeral = require("numeral");
numeral.defaultFormat("0,000");

const styles = theme => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.primary["A100"],
        overflow: "hidden",
        // background: `url(${backgroundShape}) no-repeat`,
        backgroundSize: "cover",
        backgroundPosition: "0 400px",
        marginTop: 10,
        padding: 2,
        marginBottom: 50
    },
    grid: {
        margin: `0 ${theme.spacing(2)}px`
    },
    smallContainer: {
        width: "60%"
    },
    bigContainer: {
        width: "80%"
    },
    stepContainer: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    stepGrid: {
        width: "80%"
    },
    backButton: {
        marginRight: theme.spacing(1)
    },
    outlinedButtom: {
        textTransform: "uppercase",
        margin: theme.spacing(1)
    },
    stepper: {
        backgroundColor: "transparent"
    },
    paper: {
        padding: theme.spacing(3),
        textAlign: "left",
        color: theme.palette.text.secondary
    },
    topInfo: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: 42
    },
    formControl: {
        width: "100%"
    },
    selectEmpty: {
        marginTop: theme.spacing(2)
    },
    borderColumn: {
        borderBottom: `1px solid ${theme.palette.grey["100"]}`,
        paddingBottom: 24,
        marginBottom: 24
    },
    flexBar: {
        marginTop: 32,
        display: "flex",
        justifyContent: "center"
    }
});


class UserDetails extends Component {
    state = {
        activeStep: 0,
        receivingAccount: "Home Account",
        repaimentAccount: "Saving Account",
        termsChecked: false,
        labelWidth: 0
    };

    componentDidMount() { }

    goToDashboard = event => {
        const queryString = this.props.location.search;

        this.props.history.push({
            pathname: "/",
            search: queryString
        });
    };

    render() {
        const { classes } = this.props;
        const userData = this.props.location.data;

        return (
            <React.Fragment>
                <CssBaseline />
                <div className={classes.root}>
                    <Grid container justify="center">
                        <Grid
                            spacing={1}
                            alignItems="center"
                            justify="center"
                            container
                            className={classes.grid}
                        >
                            <Grid item xs={12}>
                                <Back />
                                <div >
                                    <div >
                                        <Card>
                                            <CardContent>


                                                <Typography variant="h5" component="h2">
                                                    {userData.first_name} {userData.last_name}
                                                </Typography>
                                            </CardContent>
                                        </Card>
                                        <TableContainer component={Paper}>
                                            <Table className={classes.table} size="small" aria-label="a dense table">
                                                <TableBody>

                                                    <TableRow >

                                                    </TableRow>
                                                    <TableRow >
                                                        <TableCell align="left">Mobbile No</TableCell>
                                                        <TableCell align="right">{userData.mobile_number}</TableCell>
                                                    </TableRow>

                                                    <TableRow >
                                                        <TableCell align="left">Email id</TableCell>
                                                        <TableCell align="right">{userData.email}</TableCell>
                                                    </TableRow>

                                                    <TableRow >
                                                        <TableCell align="left">Referred By</TableCell>
                                                        <TableCell align="right">{userData.referred_by}</TableCell>
                                                    </TableRow>

                                                    <TableRow >
                                                        <TableCell align="left">DOB</TableCell>
                                                        <TableCell align="right">{userData.date_of_birth}</TableCell>
                                                    </TableRow>


                                                    <TableRow >
                                                        <TableCell align="left">Joined On</TableCell>
                                                        <TableCell align="right">{userData.created_at}</TableCell>
                                                    </TableRow>

                                                </TableBody>
                                            </Table>
                                        </TableContainer>

                                        <Card className={classes.root}>

                                            <CardActions>
                                                <Button
                                                    variant="contained"
                                                    color="primary"
                                                    className={classes.secondary}
                                                    to={{ pathname: "/payments", data: userData.id }}
                                                    component={Link}
                                                    size="small"
                                                >
                                                    Payments
                                                </Button>
                                            </CardActions>
                                        </Card>
                                    </div>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </div>
            </React.Fragment>
        );
    }
}

export default withRouter(withStyles(styles)(UserDetails));
