import React, { Component } from "react";
import withStyles from "@material-ui/styles/withStyles";
import { withRouter } from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { Config } from "./common/Config";
import AuthHandler from "./common/AuthHandler";


import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import FolderIcon from '@material-ui/icons/Folder';
import DeleteIcon from '@material-ui/icons/Delete';
import Back from "./common/Back";

const qs = require("query-string");
const backgroundShape = require("../images/shape.svg");

const numeral = require("numeral");
numeral.defaultFormat("0,000");

const styles = theme => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.primary["A100"],
        overflow: "hidden",
        background: `url(${backgroundShape}) no-repeat`,
        backgroundSize: "cover",
        backgroundPosition: "0 400px",
        marginTop: 10,
        padding: 20,
        paddingBottom: 200
    },
    grid: {
        margin: `0 ${theme.spacing(2)}px`
    },
    smallContainer: {
        width: "60%"
    },
    bigContainer: {
        width: "80%"
    },
    stepContainer: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    stepGrid: {
        width: "80%"
    },
    backButton: {
        marginRight: theme.spacing(1)
    },
    outlinedButtom: {
        textTransform: "uppercase",
        margin: theme.spacing(1)
    },
    stepper: {
        backgroundColor: "transparent"
    },
    paper: {
        padding: theme.spacing(3),
        textAlign: "left",
        color: theme.palette.text.secondary
    },
    topInfo: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: 42
    },
    formControl: {
        width: "100%"
    },
    selectEmpty: {
        marginTop: theme.spacing(2)
    },
    borderColumn: {
        borderBottom: `1px solid ${theme.palette.grey["100"]}`,
        paddingBottom: 24,
        marginBottom: 24
    },
    flexBar: {
        marginTop: 32,
        display: "flex",
        justifyContent: "center"
    }
});


class Payments extends Component {
    state = {
        activeStep: 0,
        receivingAccount: "Home Account",
        repaimentAccount: "Saving Account",
        termsChecked: false,
        labelWidth: 0
    };

    componentDidMount() { }

    componentWillMount() {
        this.getPayments()
    }

    getPayments = () => {
        // this.checkIsUserLoggedIn();
        let apiURI = '';

            apiURI = Config.API.BASE_URL + Config.API.GET_PAYMENTS + this.props.data;

        let authHandler = new AuthHandler();
        let isAuthenticated = authHandler.isAuthenticated();

        // this.getOrderCount(apiURI)

        this.setState({ loader: true });
        fetch(apiURI, {
            method: "GET",
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Content-Type": "application/json",
                "Authorization": "Bearer " + isAuthenticated,
                "cache-control": "no-cache"
            }
        }).then(response => {
            response.json()
                .then(responseJson => {

                    if (response.status === 401) {
                        this.props.history.push("/login");
                    }
                    if (!responseJson.error === null) {
                        throw responseJson;
                    }
                    console.log(responseJson.data)
                    this.setState({ data: responseJson.data });
                    this.setState({ _meta: responseJson._meta });
                    this.setState({ loader: false });
                })
                .catch(Err => {
                    if (response.status === 401) {
                        this.props.history.push("/login");
                    }
                    this.setState({ displayErrors: true });
                    this.setState({ loader: false });
                })
        }
        );
    }
    goToDashboard = event => {
        const queryString = this.props.location.search;

        this.props.history.push({
            pathname: "/",
            search: queryString
        });
    };

  generate = (element) => {
    return [0, 1, 2].map((value) =>
        React.cloneElement(element, {
            key: value,
        }),
    );
}
    render() {
        const { classes } = this.props;
        const userData = this.props.location.data;

        return (
            <React.Fragment>
                <CssBaseline />
                <div className={classes.root}>
                    <Grid container justify="center">
                        <Grid
                            spacing={1}
                            alignItems="center"
                            justify="center"
                            container
                            className={classes.grid}
                        >
                            <Grid item xs={12}>
                                <Back />
                                <div >
                                    <div >
                                        <Card className={classes.root}>
                                            <CardContent>
                                                <Typography variant="h5" component="h2">
                                                    {userData.first_name} {userData.last_name}
                                                </Typography>

                                                <Typography className={classes.title} color="textSecondary" gutterBottom>
                                                    {userData}
                                                </Typography>
                                                <Typography className={classes.pos} color="textSecondary">
                                                    {userData.mobile_number}
                                                </Typography>
                                                <Typography variant="body2" component="p">
                                                    Referred By <b>Pranav </b>.
                                                    <br />
                                                </Typography>
                                                <Typography variant="body2" component="p">
                                                    DOB : {userData.date_of_birth}
                                                    <br />
                                                </Typography>
                                                <Typography variant="body2" component="p">
                                                    Joined On : {userData.created_at}
                                                    <br />
                                                </Typography>
                                            </CardContent>
                                            <CardActions>
                                                <Button size="small" color='primary'> Payments</Button>
                                            </CardActions>
                                        </Card>
                                    </div>
                                </div>
                                <div >
                                    <Grid item xs={12} md={6}>
                                        <Typography variant="h6" className={classes.title}>
                                            Icon with text
          </Typography>
                                        <div className={classes.demo}>
                                            <List dense={true}>
                                                {generate(
                                                    <ListItem>
                                                        <ListItemIcon>
                                                            <FolderIcon />
                                                        </ListItemIcon>
                                                        <ListItemText
                                                            primary="Single-line item"
                                                            secondary={secondary ? 'Secondary text' : null}
                                                        />
                                                    </ListItem>,
                                                )}
                                            </List>
                                        </div>
        </Grid>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </div>
            </React.Fragment>
        );
    }
}

export default withRouter(withStyles(styles)(Payments));
